function ep_ajax_filters() {

    register_sidebar(array(
        'name' => 'Ajax Filter',
        'id' => 'ep_ajax_filter_before',
        'before_widget' => '<div id="sidebar" class="sidebar-categories"><h3 class="sidebar-header">Kategorie</h3>',
        'after_widget' => '</div>',
    ));
    remove_action('woocommerce_before_main_content', 'woocommerce_breadcrumb', 20);
    add_action('before_woocommerce_init', function () {
        remove_action('woocommerce_before_shop_loop', 'woocommerce_catalog_ordering', 30);
    });
}

add_action('widgets_init', 'ep_ajax_filters');

// Add widget area above Woocommerce products

add_action('woocommerce_before_shop_loop', 'woocommerce_sidebar', 40);

function woocommerce_sidebar() {
    if (is_woocommerce()) {
        echo '<div class="col-md-3"><div class="wrapper">';
        dynamic_sidebar('ep_ajax_filter_before-products');
        echo '</div></div>';
    }
}

